        </div>
      </MemoizedMarker>
    );
  }, [selectedVessel]);

  // Get visible vessel count - memoized for sidebar display
  const visibleVesselCount = useMemo(() => 
    vessels.filter(v => {
      const matchesFilter = filter === 'all' || 
                         (filter === 'russian' ? v.isRussian : v.type === filter);
      return matchesFilter;
    }).length
  , [vessels, filter]);

  // Get only vessels visible in current sidebar (both filtered and in bounds) - memoized
  const displayedVessels = useMemo(() => 
    vessels.filter(v => {
      const matchesFilter = filter === 'all' || 
                          (filter === 'russian' ? v.isRussian : v.type === filter);

      const inBounds = isInMapBounds(v.position[1], v.position[0]);

      return matchesFilter && inBounds;
    }).slice(0, 50) // Still limit to 50 for performance
  , [vessels, filter, isInMapBounds]);

  // Memoize UI elements that don't need frequent updates
  const controlPanels = useMemo(() => (
    <>
      <div className="control-panel">
        <h2 className="text-sm font-semibold mb-2 palantir-heading">Display Mode</h2>
        <div className="flex flex-row flex-nowrap">
          <button 
            className={displayMode === 'radar' ? 'active' : ''} 
            onClick={() => setDisplayMode('radar')}
          >
            <Radar size={14} className="mr-1" />
            Radar
          </button>
          <button 
            className={displayMode === 'sonar' ? 'active' : ''} 
            onClick={() => setDisplayMode('sonar')}
          >
            <Waves size={14} className="mr-1" />
            Sonar
          </button>
          <button 
            className={displayMode === 'fused' ? 'active' : ''} 
            onClick={() => setDisplayMode('fused')}
          >
            <Layers size={14} className="mr-1" />
            Fused
          </button>
        </div>
      </div>

      <div className="control-panel">
        <h2 className="text-sm font-semibold mb-2 palantir-heading">Vessel Filter</h2>
        <div className="flex flex-row flex-wrap">
          <button 
            className={filter === 'all' ? 'active' : ''} 
            onClick={() => setFilter('all')}
          >
            All
          </button>
          <button 
            className={filter === 'commercial' ? 'active' : ''} 
            onClick={() => setFilter('commercial')}
          >
            Commercial
          </button>
          <button 
            className={filter === 'military' ? 'active' : ''} 
            onClick={() => setFilter('military')}
          >
            Military
          </button>
          <button 
            className={filter === 'submarine' ? 'active' : ''} 
            onClick={() => setFilter('submarine')}
          >
            Submarines
          </button>
          <button 
            className={filter === 'russian' ? 'active' : ''} 
            onClick={() => setFilter('russian')}
          >
            Russian
          </button>
        </div>
      </div>

      <div className="control-panel">
        <h2 className="text-sm font-semibold mb-3 palantir-heading">Wind Infrastructure</h2>
        <div className="flex flex-col gap-2">
          <button 
            className={showWindFarms ? 'active' : ''}
            onClick={() => {
              const newShowWindFarms = !showWindFarms;
              setShowWindFarms(newShowWindFarms);
              // If turning off wind farms, ensure radar coverage is also off
              if (!newShowWindFarms) {
                setShowRadarCoverage(false);
              }
            }}
          >
            <Wind size={16} className="mr-1" />
            Wind Farms {showWindFarms ? '(Shown)' : '(Hidden)'}
          </button>
          <button 
            className={showRadarCoverage ? 'active' : ''}
            onClick={() => {
              setShowRadarCoverage(!showRadarCoverage);
            }}
            disabled={!showWindFarms}
            style={{ opacity: !showWindFarms ? 0.5 : 1 }}
          >
            <Radar size={16} className="mr-1" />
            Radar Coverage (42 NM)
          </button>
        </div>
      </div>

      <div className="control-panel">
        <h2 className="text-sm font-semibold mb-3 palantir-heading">Commercial Vessel Infrastructure</h2>
        <div className="flex flex-col gap-2">
          <button 
            className={showVesselRadar ? 'active' : ''}
            onClick={() => setShowVesselRadar(!showVesselRadar)}
          >
            <Radar size={16} className="mr-1" />
            Vessel Radar (GT 300-2999)
          </button>
          <button 
            className={showLargeVesselRadar ? 'active' : ''}
            onClick={() => setShowLargeVesselRadar(!showLargeVesselRadar)}
          >
            <Radar size={16} className="mr-1" />
            Vessel Radar (GT &gt; 3000)
          </button>
        </div>
      </div>

      {/* Add simulation controls panel */}
      <div className="control-panel">
        <h2 className="text-sm font-semibold mb-3 palantir-heading">Vessel Simulation</h2>
        <div className="flex flex-col gap-3">
          <button 
            className={simulationEnabled ? 'active' : ''}
            onClick={() => setSimulationEnabled(!simulationEnabled)}
          >
            <Ship size={16} className="mr-1" />
            {simulationEnabled ? 'Pause Movement' : 'Start Movement'}
          </button>
          <div className="flex flex-col">
            <span className="text-xs mb-1">Speed: {simulationSpeed}x</span>
            <input 
              type="range" 
              min="1" 
              max="50" 
              value={simulationSpeed}
              onChange={(e) => setSimulationSpeed(parseInt(e.target.value))}
              className="w-2/5 max-w-[80px] accent-blue-500"
            />
          </div>

          <button 
            className={`${seaMeshSimulationEnabled ? 'active' : ''} bg-amber-700 hover:bg-amber-600`}
            onClick={toggleSeaMeshSimulation}
          >
            <Radar size={16} className="mr-1" />
            {seaMeshSimulationEnabled ? 'Stop SeaMesh' : 'Simulate SeaMesh'}
          </button>
        </div>
      </div>
    </>
  ), [displayMode, filter, showWindFarms, showRadarCoverage, showVesselRadar, showLargeVesselRadar, simulationEnabled, simulationSpeed, seaMeshSimulationEnabled]);

  // Add debugging for selectedVessel state changes
  useEffect(() => {
    console.log("selectedVessel updated:", selectedVessel);

    // Add debugging to check if selectedVessel is correctly set
    if (selectedVessel) {
      console.log("Selected vessel details:", {
        id: selectedVessel.id,
        name: selectedVessel.name,
        type: selectedVessel.type,
        isWindFarm: selectedVessel.isWindFarm
      });

      // Force a small UI update to trigger re-renders
      const refreshTimer = setTimeout(() => {
        // This just forces a small UI update
        const dummyEvent = new Event('resize');
        window.dispatchEvent(dummyEvent);
      }, 100);

      return () => clearTimeout(refreshTimer);
    }
  }, [selectedVessel]);

  // Function to stop all active simulations
  const stopAllSimulations = useCallback(() => {
    // Stop vessel movement simulation
    if (simulationEnabled) {
      setSimulationEnabled(false);
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
        animationFrameRef.current = null;
      }
    }

    // Stop SeaMesh simulation
    if (seaMeshSimulationEnabled) {
      setSeaMeshSimulationEnabled(false);
      if (seaMeshAnimationRef.current) {
        clearInterval(seaMeshAnimationRef.current);
        seaMeshAnimationRef.current = null;
      }
    }
  }, [simulationEnabled, seaMeshSimulationEnabled]);

  // Initialize warships for SeaMesh simulation
  const initializeSeaMeshSimulation = useCallback(() => {
    // Define locations
    const kaliningrad = { lat: 54.7, lng: 20.5 };
    const stPetersburg = { lat: 59.9, lng: 30.3 };
    const stockholm = { lat: 59.3, lng: 18.1 };

    // Create Russian warships - one from Kaliningrad, one from St. Petersburg
    const newWarships = [
      {
        id: 'warship-kaliningrad',
        name: 'RFS Admiral Gorshkov',
        type: 'military',
        position: [kaliningrad.lng, kaliningrad.lat],
        heading: 315, // Heading toward Stockholm
        speed: 0,
        targetSpeed: 20,
        isRussian: true,
        flag: 'Russia',
        isWarship: true,
        detected: false,
        path: [
          kaliningrad,
          { lat: 55.3, lng: 19.2 }, // Baltic Sea route point 1
          { lat: 56.5, lng: 18.4 }, // Baltic Sea route point 2
          { lat: 57.9, lng: 18.2 }, // South of Gotland
          { lat: 58.6, lng: 18.3 }, // North of Gotland
          stockholm
        ],
        currentPathIndex: 0,
        radar: false
      },
      {
        id: 'warship-stpetersburg',
        name: 'RFS Steregushchiy',
        type: 'military',
        position: [stPetersburg.lng, stPetersburg.lat],
        heading: 270, // Heading west toward Stockholm
        speed: 0,
        targetSpeed: 25,
        isRussian: true,
        flag: 'Russia',
        isWarship: true,
        detected: false,
        path: [
          stPetersburg,
          { lat: 59.8, lng: 27.0 }, // Gulf of Finland route point 1
          { lat: 59.7, lng: 24.5 }, // Gulf of Finland route point 2
          { lat: 59.5, lng: 22.0 }, // Gulf of Finland exit
          { lat: 59.4, lng: 20.0 }, // Baltic Sea approach
          stockholm
        ],
        currentPathIndex: 0,
        radar: false
      }
    ];

    setWarships(newWarships);
    setDetectionRings([]);
    setSimulationStep(0);

    // Center map on Gulf of Finland
    if (mapRef.current) {
      mapRef.current.panTo({ lat: 58.5, lng: 21.0 });
      mapRef.current.setZoom(7);
    }

    return newWarships;
  }, []);

  // Run SeaMesh simulation steps - simplified version
  const runSeaMeshSimulation = useCallback(() => {
    if (!seaMeshSimulationEnabled) return;

    setSimulationStep(prevStep => {
      const nextStep = prevStep + 1;

      // Move warships along their paths - simplified movement logic
      setWarships(prevWarships => {
        return prevWarships.map(warship => {
          // Only start moving after a few steps (staggered start)
          const shouldMove = (warship.id === 'warship-kaliningrad' && nextStep > 3) || 
                            (warship.id === 'warship-stpetersburg' && nextStep > 8);

          if (!shouldMove) return warship;

          // If we reached target, stop moving
          if (warship.currentPathIndex >= warship.path.length - 1) {
            return { ...warship, speed: 0, targetSpeed: 0 };
          }

          // Simple direct movement to next path point
          const currentPoint = warship.path[warship.currentPathIndex];
          const nextPoint = warship.path[warship.currentPathIndex + 1];

          // Calculate new heading
          const dx = nextPoint.lng - currentPoint.lng;
          const dy = nextPoint.lat - currentPoint.lat;
          const heading = Math.atan2(dx, dy) * (180 / Math.PI);

          // Simple fixed progress per step (less complex math)
          const progress = 0.2; // Move 20% closer to the next point each step

          // Calculate new position
          const newLng = currentPoint.lng + dx * progress;
          const newLat = currentPoint.lat + dy * progress;

          // Check if we've reached the next point
          const distanceToNext = Math.sqrt(
            Math.pow(nextPoint.lng - newLng, 2) + 
            Math.pow(nextPoint.lat - newLat, 2)
          );

          // If we're close enough to the next point, move to next segment
          if (distanceToNext < 0.01) {
            return {
              ...warship,
              position: [nextPoint.lng, nextPoint.lat],
              currentPathIndex: warship.currentPathIndex + 1,
              heading: heading,
              speed: 20,
            };
          }

          // Otherwise, continue moving along current segment
          return {
            ...warship,
            position: [newLng, newLat],
            heading: heading,
            speed: 20,
          };
        });
      });

      // Add detection rings at specific steps - simplified
      if (nextStep === 15 || nextStep === 30 || nextStep === 45 || nextStep === 60) {
        console.log("Detection step triggered:", nextStep);

        // Get current warship positions
        const kaliningradWarship = warships.find(w => w.id === 'warship-kaliningrad');
        const stPetersburgWarship = warships.find(w => w.id === 'warship-stpetersburg');

        // Add detection ring to a random vessel
        if (vessels.length > 0 && (kaliningradWarship || stPetersburgWarship)) {
          console.log("Found vessels and warships");
          // Choose a random commercial vessel
          const commercialVessels = vessels.filter(v => 
            (v.type === 'commercial' || v.type === 'tanker') && v.grossTonnage >= 300
          );

          if (commercialVessels.length > 0) {
            console.log("Found commercial vessels:", commercialVessels.length);
            const randomIndex = Math.floor(Math.random() * commercialVessels.length);
            const vessel = commercialVessels[randomIndex];

            // Choose which warship to detect (alternate)
            const targetWarship = nextStep % 2 === 0 ? kaliningradWarship : stPetersburgWarship;

            if (targetWarship) {
              console.log("Creating detection for warship:", targetWarship.id);
              // Create a detection ring
              const newRing = {
                id: `detection-${Date.now()}`,
                position: { lat: vessel.position[1], lng: vessel.position[0] },
                timestamp: Date.now(),
                range: Math.floor(Math.random() * 10) + 15 // 15-25 NM
              };

              // Add the ring
              console.log("Adding new detection ring");
              setDetectionRings(prev => [...prev, newRing]);

              // Mark warship as detected
              setWarships(prev => 
                prev.map(w => w.id === targetWarship.id ? {...w, detected: true, radar: true} : w)
              );
            }
          }
        }
      }

      // End simulation after a certain number of steps
      if (nextStep > 80) {
        console.log("Simulation complete - ending");
        if (seaMeshAnimationRef.current) {
          console.log("Clearing animation interval");
          clearInterval(seaMeshAnimationRef.current);
          seaMeshAnimationRef.current = null;
        }

        // Use a timeout to avoid state update during render
        setTimeout(() => {
          console.log("Setting seaMeshSimulationEnabled to false");
          setSeaMeshSimulationEnabled(false);
        }, 0);
      }

      return nextStep;
    });
  }, [seaMeshSimulationEnabled, vessels, warships, setWarships, setDetectionRings, setSeaMeshSimulationEnabled, seaMeshAnimationRef]);

  // Function to toggle SeaMesh simulation
  const toggleSeaMeshSimulation = useCallback(() => {
    if (seaMeshSimulationEnabled) {
      // Stop simulation
      console.log("Stopping SeaMesh simulation");
      setSeaMeshSimulationEnabled(false);
    } else {
      // Stop other simulations first
      if (simulationEnabled) {
        console.log("Stopping vessel movement simulation before starting SeaMesh");
        setSimulationEnabled(false);
        if (animationFrameRef.current) {
          cancelAnimationFrame(animationFrameRef.current);
          animationFrameRef.current = null;
        }
      }

      // Initialize warships (reset if already exists)
      console.log("Initializing warships for SeaMesh simulation");
      const initialWarships = initializeSeaMeshSimulation();
      console.log("Initial warships created:", initialWarships);

      // Start simulation
      console.log("Starting SeaMesh simulation");
      setSeaMeshSimulationEnabled(true);
    }
  }, [seaMeshSimulationEnabled, simulationEnabled, initializeSeaMeshSimulation, setSeaMeshSimulationEnabled, setSimulationEnabled]);

  // Start/stop SeaMesh simulation
  useEffect(() => {
    console.log("SeaMesh simulation enabled changed:", seaMeshSimulationEnabled);
    console.log("Current warships:", warships);

    if (seaMeshSimulationEnabled) {
      // Initialize warships if not already done
      if (warships.length === 0) {
        console.log("No warships found, initializing...");
        const initialWarships = initializeSeaMeshSimulation();
        console.log("Warships initialized:", initialWarships);

        // Ensure warships are set properly before starting the animation
        if (initialWarships && initialWarships.length > 0) {
          console.log("Starting animation timer");
          // Run simulation every 2 seconds
          seaMeshAnimationRef.current = setInterval(() => {
            console.log("Animation step triggered");
            runSeaMeshSimulation();
          }, 2000);
        } else {
          console.error("Failed to initialize warships");
        }
      } else {
        console.log("Warships already initialized, just starting animation");
        // Just start the animation if warships are already initialized
        seaMeshAnimationRef.current = setInterval(() => {
          console.log("Animation step triggered");
          runSeaMeshSimulation();
        }, 2000);
      }

      // Show vessel radar automatically
      console.log("Enabling vessel radar for simulation");
      setShowVesselRadar(true);
    } else if (seaMeshAnimationRef.current) {
      console.log("Clearing SeaMesh animation interval");
      clearInterval(seaMeshAnimationRef.current);
      seaMeshAnimationRef.current = null;
    }

    return () => {
      if (seaMeshAnimationRef.current) {
        console.log("Cleanup: clearing SeaMesh animation interval");
        clearInterval(seaMeshAnimationRef.current);
        seaMeshAnimationRef.current = null;
      }
    };
  }, [seaMeshSimulationEnabled, initializeSeaMeshSimulation, runSeaMeshSimulation, warships.length, setShowVesselRadar]);

  // Render the SeaMesh simulation warships and detection rings
  const renderSeaMeshSimulation = useCallback(() => {
    if (warships.length === 0) return null;

    return (
      <>
        {/* Render the warships */}
        {warships.map(warship => {
          const position = { lat: warship.position[1], lng: warship.position[0] };

          // Create warship marker icon
          const warshipIcon = {
            path: "M -1,1 L 1,1 L 1,-1 L -1,-1 Z", // Simple square
            fillColor: 'rgb(231, 76, 60)', // Red color for Russian vessels
            fillOpacity: 0.9,
            strokeColor: '#FFFFFF',
            strokeWeight: 1,
            scale: 5,
            rotation: warship.heading,
          };

          return (
            <React.Fragment key={warship.id}>
              <MemoizedMarker
                position={position}
                icon={warshipIcon}
                zIndex={100}
                onClick={() => setSelectedVessel(warship)}
              />

              {/* Show radar coverage if detected */}
              {warship.radar && (
                <MemoizedCircle
                  center={position}
                  radius={40 * 1852} // 40 NM in meters
                  options={{
                    fillColor: '#FF5252',
                    fillOpacity: 0.05,
                    strokeColor: '#FF5252',
                    strokeOpacity: 0.7,
                    strokeWeight: 1,
                    strokeDashArray: [5, 5], // Dashed line pattern
                  }}
                />
              )}

              {/* Show path line for warships */}
              <MemoizedPolyline
                path={warship.path.map(point => ({ lat: point.lat, lng: point.lng }))}
                options={{
                  strokeColor: '#FF5252',
                  strokeOpacity: 0.6,
                  strokeWeight: 1,
                  icons: [{
                    icon: {
                      path: 'M 0,-1 0,1',
                      strokeColor: '#FF5252',
                      strokeOpacity: 1,
                      scale: 3
                    },
                    offset: '0',
                    repeat: '15px'
                  }]
                }}
              />
            </React.Fragment>
          );
        })}

        {/* Render detection rings */}
        {detectionRings.map((ring, index) => {
          // Calculate pulse animation - rings should pulse/flash
          const animationProgress = (Date.now() - ring.timestamp) % 3000 / 3000;
          const opacity = 0.7 - animationProgress * 0.5;
          const radius = ring.range * 1852; // Convert NM to meters

          return (
            <MemoizedCircle
              key={ring.id}
              center={ring.position}
              radius={radius}
              options={{
                fillColor: '#4CAF50',
                fillOpacity: opacity * 0.1,
                strokeColor: '#4CAF50',
                strokeOpacity: opacity,
                strokeWeight: 2,
              }}
            />
          );
        })}
      </>
    );
  }, [warships, detectionRings, setSelectedVessel]);

  // Update UI to include display mode toggle
  return (
    <div className="baltic-tracker-container">
      <div className="flex h-screen">
        {showSidebar && (
          <div className="sidebar w-80">
            <div className="flex items-center justify-between mb-6">
              <h1 className="text-xl font-semibold palantir-heading">Baltic Sea Tracker</h1>
              <button onClick={() => setShowSidebar(false)} className="text-gray-400 hover:text-white">
                <Menu size={20} />
              </button>
            </div>

            {controlPanels}
          </div>
        )}

        <div className="flex-1 relative">
          {!showSidebar && (
            <button
              className="absolute top-4 left-4 z-10 bg-gray-800 p-2 rounded-md hover:bg-gray-700"
              onClick={() => setShowSidebar(true)}
            >
              <Menu size={20} />
            </button>
          )}

          {/* Simplified Google Maps integration */}
          {loadError && (
            <div className="w-full h-full flex flex-col items-center justify-center bg-gray-900 text-white p-4">
              <div className="text-xl mb-4">Error loading Google Maps: {loadError.message}</div>
            </div>
          )}

          {!loadError && isLoaded ? (
            <GoogleMap
              id="map"
              mapContainerStyle={mapContainerStyle}
              center={mapCenter}
              zoom={mapZoom}
              options={mapOptions}
              onLoad={onMapLoad}
              onUnmount={onMapUnmount}
              onClick={handleMapClick}
              onZoomChanged={handleZoomChanged}
              onBoundsChanged={handleBoundsChanged}
              key={mapKey}
            >
              {/* Render basemap features */}
              {renderWindFarms()}
              {renderRadarCoverage()}
              {renderVesselRadarCoverage()}

              {/* Render vessels */}
              {renderVessels()}

              {/* Render the SeaMesh simulation if enabled */}
              {seaMeshSimulationEnabled && renderSeaMeshSimulation()}

              {/* Show selection details as a tooltip */}
              {selectedVessel && renderSelectedVesselDetails()}
            </GoogleMap>
          ) : !loadError ? (
            <div className="w-full h-full flex items-center justify-center bg-gray-900 text-white">
              <div className="text-xl">Loading map...</div>
            </div>
          ) : null}

          <div className="absolute bottom-4 right-4 flex gap-2">
            <button 
              onClick={() => zoomRef.current && zoomRef.current.zoomIn()}
              className="bg-gray-800 hover:bg-gray-700 p-2 rounded-md shadow-lg"
            >
              <ZoomIn size={20} className="text-blue-400" />
            </button>
            <button 
              onClick={() => zoomRef.current && zoomRef.current.zoomOut()}
              className="bg-gray-800 hover:bg-gray-700 p-2 rounded-md shadow-lg"
            >
              <ZoomOut size={20} className="text-blue-400" />
            </button>
          </div>

          {/* Move the Reset filters button to bottom-right corner */}
          <button
            onClick={() => {
              // Reset vessel selection and visualization features
              setSelectedVessel(null);
              setShowWindFarms(false);
              setShowRadarCoverage(false);
              setShowVesselRadar(false);
              setShowLargeVesselRadar(false);
              setSimulationEnabled(false);

              // Reset display settings
              setDisplayMode('radar');
              setFilter('all');
              setSimulationSpeed(10);

              // Reset map position and zoom
              if (mapRef.current) {
                mapRef.current.setCenter(defaultCenter);
                mapRef.current.setZoom(6);
                setCurrentZoom(6);
                setCurrentMapCenter(defaultCenter);
              }

              // Force React to remount map components
              setMapKey(Date.now());

              // Small timeout to ensure map refreshes completely
              setTimeout(() => {
                if (mapRef.current && mapRef.current.overlayMapTypes) {
                  mapRef.current.overlayMapTypes.clear();
                }
              }, 10);
            }}
            style={{
              position: 'absolute',
              bottom: '20px',
              right: '20px',
              backgroundColor: '#8B0000',
              color: '#FFFFFF',
              padding: '8px 16px',
              borderRadius: '4px',
              border: 'none',
              fontWeight: 'normal',
              cursor: 'pointer',
              boxShadow: '0 2px 4px rgba(0, 0, 0, 0.5)',
              zIndex: 9999,
              fontSize: '14px'
            }}
          >
            Reset filters
          </button>
        </div>
      </div>

      <div className="bg-gray-800 p-2 text-center text-sm text-gray-400">
        <div className="flex justify-center items-center">
          <Info size={16} className="mr-1 text-blue-400" />
          <span className="text-gray-300">Baltic Sea Tracker - {filter === 'all' ? 300 : vessels.filter(v => filter === 'russian' ? v.isRussian : v.type === filter).length} vessels in {displayMode} mode | Zoom: {currentZoom}</span>
          {showWindFarms && <span className="mx-1 text-amber-400">| Wind Farms Shown</span>}
          {showRadarCoverage && showWindFarms && <span className="mx-1 text-red-400">| Radar Coverage (42 NM)</span>}
          {showVesselRadar && <span className="mx-1 text-blue-400">| Vessel Radar (20/40 NM)</span>}
          {simulationEnabled && <span className="mx-1 text-green-400">| Vessel Movement ({simulationSpeed}x)</span>}
          {seaMeshSimulationEnabled && <span className="mx-1 text-amber-400">| SeaMesh Simulation Active</span>}
        </div>
      </div>
    </div>
  );
}

export default BalticSeaTracker;
